const InteractiveMap: React.FC = () => {
  const [selectedCharacter, setSelectedCharacter] = useState<Character | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const scrollRef = useRef<HTMLDivElement>(null);
  const modalRef = useRef<HTMLDivElement>(null);

  const handleCharacterClick = (character: Character) => {
    setSelectedCharacter(character);
  };

  const handleCloseSidebar = () => {
    setSelectedCharacter(null);
  };

  const scroll = (direction: 'left' | 'right') => {
    if (scrollRef.current) {
      const scrollAmount = direction === 'left' ? -200 : 200; // Adjust scroll amount as needed
      scrollRef.current.scrollBy({ left: scrollAmount, behavior: 'smooth' });
    }
  };

  const filteredCharacters = characters.filter((character) =>
    character.name.toLowerCase().includes(searchQuery.toLowerCase())
  );
  // Close modal when clicking outside of it
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (modalRef.current && !modalRef.current.contains(event.target as Node)) {
        handleCloseSidebar();
      }
    };

    
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="relative w-full h-[500px] bg-gray-100 dark:bg-gray-900 rounded-lg overflow-hidden shadow-lg">
      {/* Search Bar */}
      <div className="p-4">
        <input
          type="text"
          placeholder="Search characters..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg dark:border-gray-600 dark:bg-gray-800 dark:text-white placeholder-gray-500"
        />
      </div>

      {/* Left Scroll Button */}
      <button
        className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-blue-600 dark:bg-blue-500 text-white rounded-full p-3 shadow-lg z-10 hover:bg-blue-700 dark:hover:bg-blue-600 transition-all"
        onClick={() => scroll('left')}
      >
        <FaChevronLeft size={20} /> {/* Left Arrow */}
      </button>

      <div ref={scrollRef} className="flex overflow-x-auto space-x-4 p-4 scrollbar-hide">
        {filteredCharacters.map((character) => (
          <motion.div
            key={character.name}
            className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-4 cursor-pointer hover:shadow-2xl transform transition-transform hover:scale-105 w-60 flex-none"
            onClick={() => handleCharacterClick(character)}
            whileHover={{ scale: 1.05 }}
          >
            <img src={character.imageUrl} alt={character.name} className="w-full h-40 object-cover rounded-md" />
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mt-2">{character.name}</h3>
            <p className="text-gray-600 dark:text-gray-400">{character.role}</p>
            <p className="text-gray-500 dark:text-gray-400">Student: {character.studentName}</p>
          </motion.div>
        ))}
      </div>

      {/* Right Scroll Button */}
      <button
        className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-blue-600 dark:bg-blue-500 text-white rounded-full p-3 shadow-lg z-10 hover:bg-blue-700 dark:hover:bg-blue-600 transition-all"
        onClick={() => scroll('right')}
      >
        <FaChevronRight size={20} /> {/* Right Arrow */}
      </button>

      {selectedCharacter && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <motion.div
            ref={modalRef}
            className="bg-white dark:bg-gray-800 rounded-lg p-6 w-11/12 md:w-1/3 shadow-lg relative"
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            exit={{ scale: 0 }}
          >
            <button
              onClick={handleCloseSidebar}
              className="absolute top-2 right-2 text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300"
            >
              &times;
            </button>
            <h2 className="text-xl font-bold text-gray-900 dark:text-white">{selectedCharacter.name}</h2>
            <p className="text-gray-600 dark:text-gray-400 mt-2">{selectedCharacter.info}</p>
            <p className="text-gray-600 dark:text-gray-400 mt-1">
              Location: <span className="font-semibold">{selectedCharacter.geoLocation}</span>
            </p>
            <img
              src={selectedCharacter.imageUrl}
              alt={selectedCharacter.name}
              className="w-full h-40 object-cover rounded-md mt-4"
            />

            <div className="flex justify-around mt-4">
              <a href={selectedCharacter.googleMapsUrl} target="_blank" rel="noopener noreferrer" className="flex items-center justify-center w-10 h-10 bg-blue-500 text-white rounded-full hover:bg-blue-600 transition-colors">
                <FaMapMarkerAlt size={16} />
              </a>
              <a href={selectedCharacter.headquartersUrl} target="_blank" rel="noopener noreferrer" className="flex items-center justify-center w-10 h-10 bg-green-500 text-white rounded-full hover:bg-green-600 transition-colors">
                <FaBookOpen size={16} />
              </a>
              <a href={selectedCharacter.unRoleWikiUrl} target="_blank" rel="noopener noreferrer" className="flex items-center justify-center w-10 h-10 bg-yellow-500 text-white rounded-full hover:bg-yellow-600 transition-colors">
                <FaWikipediaW size={16} />
              </a>
              <a href={selectedCharacter.countryWikiUrl} target="_blank" rel="noopener noreferrer" className="flex items-center justify-center w-10 h-10 bg-purple-500 text-white rounded-full hover:bg-purple-600 transition-colors">
                <FaWikipediaW size={16} />
              </a>
            </div>
          </motion.div>
        </div>
      )}
    </div>
  );
};

export default InteractiveMap;
